import React, { useState, useEffect } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

const App = () => {
  const [apiData, setApiData] = useState(null);
  // eslint-disable-next-line no-unused-vars
  const [socket, setSocket] = useState(null);

  // Establish WebSocket connection
  useEffect(() => {
    const newSocket = io(process.env.REACT_APP_HOST);
    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
    };
  }, []);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST+'/api/ping/check-cctv');
        console.log(response.data); 
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>CCTV Status</h1>
      {apiData ? (
        <div>
          <table>
            <thead>
              <tr>
                <th>Room</th>
                <th>IP Address</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {apiData.data.map((room, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td colSpan="3">{room.room}</td>
                  </tr>
                  {room.data.map((item, itemIndex) => (
                    <tr key={`${index}-${itemIndex}`}>
                      <td></td>
                      <td>{item.ipAddress}</td>
                      <td>{item.status}</td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default App;
