        {apiData && apiData.data.map(room => (
          <tr key={room.room}>
            <td>{room.room}</td>
            <td>
              <table>
                <tbody>
                  <tr>
                    {room.data.map((cctv, index) => (
                      <React.Fragment key={cctv.ip}>
                        <td>{cctv.ip}:{cctv.status}</td>

                      </React.Fragment>
                    ))}
                  </tr>
                </tbody>
              </table>
            </td>
            <td>
              {/* <table>
                <tbody>
                  <tr>
                    {room.data.map((cctv, index) => (
