import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST+'/api/ping/check-cctv');
        console.log(response.data); 
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Connect to WebSocket server
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); // ต้องเปลี่ยนเป็น URL ของ WebSocket server ของคุณ

    console.log('WebSocket :',ws)
    // Event listener เมื่อมีข้อมูลถูกส่งมาจาก WebSocket server
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setWebSocketData(data);
    };

    // จัดการเมื่อ WebSocket connection ถูกปิด
    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    // คำสั่งที่จะถูกเรียกเมื่อ component ถูก unmount
    return () => {
      ws.close();
    };
  }, []);

  return (
    <div>
      <h1>CCTV Status</h1>
      {apiData ? (
        <div>
          <table>
            <thead>
              <tr>
                <th>Room</th>
                <th>IP Address</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {apiData.data.map((room, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td colSpan="3">{room.room}</td>
                  </tr>
                  {room.data.map((item, itemIndex) => (
                    <tr key={`${index}-${itemIndex}`}>
                      <td></td>
                      <td>{item.ip}</td>
                      <td>{item.status}</td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <h2>WebSocket Data</h2>
      <p>{webSocketData ? webSocketData.message : "Waiting for WebSocket data..."}</p>
    </div>
  );
};

export default App;
