    <div>
      <h1>CCTV Status</h1>
    {apiData && apiData.data.map(room => (
      <div key={room.room}>
        <h2>{room.room}</h2>
        <ul>
          {room.data.map(cctv => (
            <li key={cctv.ip}>
              IP: {cctv.ip}, Status: {cctv.status}
            </li>
          ))}
        </ul>
      </div>
    ))}
    {webSocketData && <p>WebSocket Data: {JSON.stringify(webSocketData)}</p>}


    </div>
  );
};

export default App;
