import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import '../assets/cctv.css';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST+'/api/ping/check-cctv');
const ipData = response.data.map(room => room.data.map(item => item.ip));
        setIpData(ipData.flat()); // แปลงเป็น array 1 มิติ
           setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);


  // Connect to WebSocket server
useEffect(() => {
  const ws = new WebSocket('ws://localhost:4000');

  console.log('WebSocket :', ws);

  ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    setWebSocketData(data);
    // console.log('set :',data)
  };

  ws.onclose = () => {
    console.log('WebSocket connection closed');
  };

  ws.onopen = () => {
    console.log('WebSocket connection opened');
  };

  if (ws.readyState === WebSocket.CLOSED) {
    console.log('WebSocket connection closed before it was established');
  }

  return () => {
    ws.close();
  };
}, 

[]); 







  return (
    <div>
      <h1>CCTV Status</h1>
      {apiData ? (
        <div>
          <table>
            <thead>
              <tr>
                <th>Room</th>
                <th>IP Address</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {apiData.data.map((room, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td colSpan="3">{room.room}</td>
                  </tr>
                  {room.data.map((item, itemIndex) => (
                    <tr key={`${index}-${itemIndex}`}>
                      <td>{item.ip}</td>
                      <td>{item.status}</td>
                  </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <h2>WebSocket Data</h2>
      <p>{webSocketData ? webSocketData.message : "Waiting for WebSocket data..."}</p>
    </div>
  );
};

export default App;
