import React, { useState, useEffect } from "react";
import axios from "axios";
import '../assets/table3.css'

const Table3 = ({ apiData2 }) => {
  const [data, setApiData] = useState(null);
  const [nameIndexMap, setNameIndexMap] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );
        setApiData(response.data);

        // Create a map to store the index of each name
        const nameMap = {};
        response.data.data.forEach((item, index) => {
          if (!nameMap[item.name]) {
            nameMap[item.name] = [];
          }
          nameMap[item.name].push(index + 1);
        });
        setNameIndexMap(nameMap);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <table className="table5">
        <thead>
          <tr>
            {data &&
              data.data.map((item) => (
                <th key={item.id} style={{ writingMode: "vertical-lr" }}>{item.name}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {data &&
            Object.keys(nameIndexMap).map((name, index) => (
              <tr key={index}>
                {nameIndexMap[name].map((idx) => (
                  <td
                    key={idx}
                    className={`status-cell ${
                      data.data[idx - 1].status === "ON" ? "status-on" : "status-off"
                    }`}
                    title={data.data[idx - 1].ip}
                    onClick={() =>
                      window.open(`http://${data.data[idx - 1].ip}`, "_blank")
                    }
                    style={{ cursor: "pointer" }}
                  >
                    <div>{idx}</div>
                  </td>
                ))}
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table3;
