import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import '../assets/cctv.css';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST+'/api/ping/check-cctv');
        console.log(process.env.REACT_APP_HOST,response.data); 
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);


  // Connect to WebSocket server
useEffect(() => {
  const ws = new WebSocket('ws://localhost:4000');

  console.log('WebSocket :', ws);

  ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    setWebSocketData(data);
    // console.log('set :',data)
  };

  ws.onclose = () => {
    console.log('WebSocket connection closed');
  };

  ws.onopen = () => {
    console.log('WebSocket connection opened');
  };

  if (ws.readyState === WebSocket.CLOSED) {
    console.log('WebSocket connection closed before it was established');
  }

  return () => {
    ws.close();
  };
}, 

[]); 

