    <div>
      <h1>CCTV Status</h1>
    <table>
      <thead>
        <tr>
          <th>Room</th>
          <th>IP</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        {apiData && apiData.data.map(room => (
          room.data.map(cctv => (
            <tr key={cctv.ip}>
              {room.data.indexOf(cctv) === 0 && <td rowSpan={room.data.length}>{room.room}</td>}
              <td>
                {cctv.ip},
                {cctv.status}
              </td>

            </tr>
          ))
        ))}
      </tbody>
    </table>
    {webSocketData && <p>WebSocket Data: {JSON.stringify(webSocketData)}</p>}


    </div>
  );
};

export default App;
