import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/cctv.css';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);

  useEffect(() => {
    fetchData();
    connectWebSocket();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_HOST}/api/ping/check-cctv`);
      setApiData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const connectWebSocket = () => {
    const ws = new WebSocket(process.env.REACT_APP_WS_HOST);

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setWebSocketData(data);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onopen = () => {
      console.log('WebSocket connection opened');
    };

    if (ws.readyState === WebSocket.CLOSED) {
      console.log('WebSocket connection closed before it was established');
    }

    return () => {
      ws.close();
    };
  };

  const formatData = (data) => {
    return data.map(room => ({
      room: room.room,
      data: room.data.flatMap(block => block.ip.map((ip, index) => ({
        ip: ip,
        status: block.status[index]
      })))
    }));
  };

  return (
    <div>
      <h1>CCTV Status</h1>
      {apiData ? (
        <div>
          <table>
            <thead>
              <tr>
                <th>Room</th>
                <th>IP Address</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {formatData(apiData).map((room, index) => (
                <React.Fragment key={index}>
                  <tr>
                    <td colSpan="3">{room.room}</td>
                  </tr>
                  {room.data.map((item, itemIndex) => (
                    <tr key={`${index}-${itemIndex}`}>
                      <td></td>
                      <td>{item.ip}</td>
                      <td>{item.status}</td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <h2>WebSocket Data</h2>
      <p>{webSocketData ? webSocketData.message : "Waiting for WebSocket data..."}</p>
    </div>
  );
};

export default App;
