import React, { useState, useEffect } from "react";
import axios from "axios";

const Table3 = ({ apiData2 }) => {
  const [data, setApiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );
        setApiData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Table3</h1>
      <table>
        <thead>
          <tr>
            {data &&
              data.data.map((item) => (
                <th key={item.name} style={{ writingMode: "vertical-lr" }}>
                  {item.name}
                </th>
              ))}
          </tr>
        </thead>
        <tbody>
          <tr>
            {data &&
              data.data.map((item, index) => (
                <td key={index}>
                  <table>
                    <tbody>
                      {item.status.map((camera, index) => (
                        <tr key={index}>
                          <td
                            className={`status-cell ${
                              camera.camera === "ON"
                                ? "status-on"
                                : "status-off"
                            }`}
                            title={camera.ip}
                            onClick={() =>
                              window.open(`http://${camera.ip}`, "_blank")
                            }
                            style={{ cursor: "pointer" }}
                          >
                            <div>{index + 1}</div> {/* แสดงเลขลำดับ */}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </td>
              ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Table3;
