import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [apiData, setApiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/ping/check-cctv');
        console.log(response.data); // Log the API response data here

        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
    <h1>API Response</h1>
    {apiData ? (
      <div>
        {/* <p>Success: {apiData.success.toString()}</p>
        <p>Message: {apiData.message}</p>
        <p>Results: {apiData.results}</p>
        <h2>Data:</h2> */}
        <table>
          <thead>
            <tr>
              <th>Room</th>
              <th>IP Address</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {apiData.data.map((room, index) => (
              <React.Fragment key={index}>
                <tr>
                  <td colSpan="3">{room.room}</td>
                </tr>
                {room.data.map((item, itemIndex) => (
                  <tr key={`${index}-${itemIndex}`}>
                    <td></td>
                    <td>{item.ipAddress}</td>
                    <td>{item.status}</td>
                  </tr>
                ))}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    ) : (
      <p>Loading...</p>
    )}
  </div>
);
};


export default App;
