import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/table3.css";

const Table3 = ({ apiData2 }) => {
  const [data, setApiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );
        setApiData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };


    

    fetchData();

    
  }, []);


  // สร้าง array ของหมายเลขหน้า
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data?.data.length / 50); i++) {
    pageNumbers.push(i);
  }

  // ฟังก์ชันสำหรับการเปลี่ยนหน้า
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
    <table className="table5">
      <thead>
        <tr>
          {data && (
            data.data.map((item, index) => (
              <th key={item.id} style={{ writingMode: "vertical-lr" }}>
                {index % 50 === 0 ? index / 50 + 1 : ""}
              </th>
            ))
          )}
        </tr>
      </thead>
      <tbody>
        <tr>
          {data &&
            data.data.map((item, index) => (
              <td
                key={item.id}
                className={`block-frame ${
                  item.status === "ON" ? "status-on" : "status-off"
                }`}
                title={item.ip}
                onClick={() => window.open(`http://${item.ip}`, "_blank")}
                style={{ cursor: "pointer" }}
              >
                <div>{item.ip}</div>
              </td>
            ))}
        </tr>
      </tbody>
    </table>

    {/* แสดง navigation bar */}
    <div className="pagination">
      {pageNumbers.map((number) => (
        <button key={number} onClick={() => handlePageChange(number)}>
          {number}
        </button>
      ))}
    </div>
  </div>
  );
};

export default Table3;
