import React, { useState, useEffect } from "react";
import axios from "axios";
import "../assets/table3.css";

const Table3 = ({ apiData }) => {
  const [statusData, setStatusData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );
        const data = response.data.data;
        const statusObject = {};
        data.forEach((item) => {
          statusObject[item.name] = item.status;
        });
        setStatusData(statusObject);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <table className="table5">
        <thead>
          <tr>
            {apiData &&
              apiData.map((name) => (
                <th key={name} style={{ writingMode: "vertical-lr" }}>
                  {name}
                </th>
              ))}
          </tr>
        </thead>
        <tbody>
          <tr>
            {apiData &&
              apiData.map((name) => (
                <td key={name}>
                  <div className={`status-cell ${statusData && statusData[name] === "ON" ? "status-on" : "status-off"}`}>
                    {statusData && statusData[name]}
                  </div>
                </td>
              ))}
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Table3;
