//home page
import React, { useState, useEffect } from "react";
import axios from "axios";
import Table1 from "../components/cctv1";
import Table2 from "../components/cctv2";
import Table3 from "../components/cctv3";

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);
  const [totalCCTV, setTotalCCTV] = useState(0);
  const [totalOnlineCCTV, setTotalOnlineCCTV] = useState(0);
  const [totalOfflineCCTV, setTotalOfflineCCTV] = useState(0);


  console.log(process.env.REACT_APP_HOST);

  // Fetch API data
 useEffect(() => {
    const fetchData = async () => {
      try {
        const response1 = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv"
        );
        const response2 = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );

        // Calculate total and online/offline CCTV cameras
        let totalCCTVCount = 0;
        let totalOnlineCCTVCount = 0;
        let totalOfflineCCTVCount = 0;

        // Process response from check-cctv endpoint
        if (response1.data && response1.data.data) {
          response1.data.data.forEach(room => {
            if (room.roomname !== "HBM") {
              totalCCTVCount += room.status.length;
              room.status.forEach(camera => {
                if (camera.camera === "ON") {
                  totalOnlineCCTVCount++;
                } else if (camera.camera === "OFF") {
                  totalOfflineCCTVCount++;
                }
              });
            }
          });
        }

        // Process response from check-cctv2 endpoint
        if (response2.data && response2.data.results && response2.data.results.data) {
          Object.values(response2.data.results.data).forEach(phase => {
            phase.forEach(room => {
              if (room.status === "ON") {
                totalOnlineCCTVCount++;
              } else {
                totalOfflineCCTVCount++;
              }
            });
          });
        }

        // Update state with combined results
        setTotalCCTV(totalCCTVCount);
        setTotalOnlineCCTV(totalOnlineCCTVCount);
        setTotalOfflineCCTV(totalOfflineCCTVCount);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 2000);

    return () => clearInterval(intervalId);
  }, []);


  // Connect to WebSocket server
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:4000");

    console.log("WebSocket :", ws);

    ws.onclose = () => {
      console.log("WebSocket connection closed");
    };

    ws.onopen = () => {
      console.log("WebSocket connection opened");
    };

    if (ws.readyState === WebSocket.CLOSED) {
      console.log("WebSocket connection closed before it was established");
    }

    return () => {
      ws.close();
    };
  }, []);

  useEffect(() => {
    const handleMessage = (event) => {
      const data = JSON.parse(event.data);
      setWebSocketData(data);
      // console.log('set :',data)
    };

    if (webSocketData) {
      const ws = new WebSocket("ws://localhost:4000");
      ws.onmessage = handleMessage;

      return () => {
        ws.close();
      };
    }
  }, [webSocketData]);

  function YourComponent() {
    const currentDate = new Date();
    // const thaiOptions = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    // const thaiDate = currentDate.toLocaleDateString('th-TH', thaiOptions);
    const options = { day: "2-digit", month: "2-digit", year: "numeric" };
    const formattedDate = currentDate.toLocaleDateString("th-TH", options);

    const timeOptions = { hour: "2-digit", minute: "2-digit" };
    const formattedTime = currentDate.toLocaleTimeString("th-TH", timeOptions);

    return (
      <div>
        <h1>{formattedDate}</h1>
        <h2>{formattedTime}</h2>
      </div>
    );
  }

  return (
    <div className="table-container">
      <div className="img-container">
        <img src={require("../assets/jastel.jpg")} alt="Jastel Logo" />
      </div>
      <h2 className="date">
        {" "}
        <YourComponent />{" "}
      </h2>
      <br></br>
      <br></br>
      <br></br>
      <br></br>

      <h1 className="title1">Jastel CCTV Monitor</h1>

      <h2 className="title2">Total CCTV: {totalCCTV}</h2>
      <h2 className="title3">Online CCTV: {totalOnlineCCTV}</h2>
      <h2 className="title4">Offline CCTV: {totalOfflineCCTV}</h2>
      <h2 className="title5">Node center : 69</h2>



      <Table1 apiData={apiData} />
      <Table2 apiData={apiData} />
      <Table3 apiData2={apiData} />
      
    </div>
  );
};

export default App;
