import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/cctv.css';

const FloatingText = ({ text }) => {
  return <div className="floating-text">{text}</div>;
};

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [webSocketData, setWebSocketData] = useState(null);
  const [hoveredIp, setHoveredIp] = useState(null);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST + '/api/ping/check-cctv');
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 2000); // Refresh every 2 seconds

    return () => clearInterval(intervalId); // Cleanup interval on unmount

  }, []);


  // Connect to WebSocket server
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');

    console.log('WebSocket :', ws);

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    ws.onopen = () => {
      console.log('WebSocket connection opened');
    };

    if (ws.readyState === WebSocket.CLOSED) {
      console.log('WebSocket connection closed before it was established');
    }

    return () => {
      ws.close();
    };
  }, []);

  useEffect(() => {
    const handleMessage = (event) => {
      const data = JSON.parse(event.data);
      setWebSocketData(data);
      // console.log('set :',data)
    };

    if (webSocketData) {
      const ws = new WebSocket('ws://localhost:4000');
      ws.onmessage = handleMessage;

      return () => {
        ws.close();
      };
    }
  }, [webSocketData]);

  // ฟังก์ชันเมื่อเมาส์เข้าไปชี้บล็อกสถานะ
  const handleMouseEnter = (ip) => {
    setHoveredIp(ip);
  };

  // ฟังก์ชันเมื่อเมาส์ออกจากบล็อกสถานะ
  const handleMouseLeave = () => {
    setHoveredIp(null);
  };

  return (
    <div className="table-container">
      <img src={require('../assets/jastel.jpg')} alt="Jastel Logo" />
      <table className="table">
        <thead className='TTH'>
          <tr>
            {apiData && apiData.data.map(room => (
              room.roomname !== "SLG" &&
              room.roomname !== "HYI3" &&
              room.roomname !== "HYI4" &&
              room.roomname !== "OFFICE7" &&
              room.roomname !== "OFFICE6" &&
              <th key={room.roomname} className="room-name1">{room.roomname && room.roomname}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          <tr className='bb'>
            {apiData && apiData.data.map(room => (
              room.roomname !== "SLG" &&
              room.roomname !== "HYI3" &&
              room.roomname !== "HYI4" &&
              room.roomname !== "OFFICE7" &&
              room.roomname !== "OFFICE6" &&
              <td
                key={room.roomname}
                className={`status-cell ${room.status.trim() === 'ON' ? 'status-on' : 'status-off'}`}
                onMouseEnter={() => handleMouseEnter(room.ipAddresses[0])}
                onMouseLeave={handleMouseLeave}
              >
                <table className="table">
                  <tbody>
                    {room.status.split(',').map((item, index) => (
                      <tr key={index}>
                        {item.trim() === 'ON' ? (
                          <td key={index} className={`status-cell status-on`}>
                            <div>{item.trim()}</div>
                          </td>
                        ) : (
                          <td key={index} className={`status-cell status-off`}>
                            <div>{item.trim()}</div>
                          </td>
                        )}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </td>
            ))}
          </tr>
        </tbody>
      </table>

      <table className='ST'>
        <tbody>
          {apiData && apiData.data
            .filter(room => ["SLG", "HYI3", "HYI4", "OFFICE7", "OFFICE6"].includes(room.roomname))
            .map(room => (
              <tr key={room.roomname}>
                <td className="room-name2">{room.roomname}</td>
                <td className="room-status">
                  <table className="status-table">
                    <tbody>
                      <tr>
                        {room.status.split(',').map((item, index) => (
                          <td key={index} className={`status-cell ${item.trim() === 'ON' ? 'status-on' : 'status-off'}`}>
                            <div>{item.trim()}</div>
                          </td>
                        ))}
                      </tr>
                    </tbody>
                  </table>

                </td>
              </tr>
            ))}
        </tbody>
      </table>

      {hoveredIp && <FloatingText text={`IP: ${hoveredIp}`} />}
    </div>
  );
};

export default App;
