import React, { useState, useEffect } from "react";
import axios from "axios";
import '../assets/table3.css'

const Table3 = ({ apiData2 }) => {
  const [data, setApiData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          process.env.REACT_APP_HOST + "/api/ping/check-cctv2"
        );
        setApiData(response.data.data); // Set data to response.data.data
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <table className="table5">
        <thead>
          <tr>
            {data && data.map((item) => (
              <th key={item.ip} style={{ writingMode: "vertical-lr" }}>{item.name}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data && data.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td> {/* แสดงเลขลำดับ */}
              {data && data.map((subitem, subindex) => (
                <td
                  key={subindex}
                  className={`status-cell ${subitem.status === "ON" ? "status-on" : "status-off"}`}
                  title={subitem.ip}
                  onClick={() => window.open(`http://${subitem.ip}`, "_blank")}
                  style={{ cursor: "pointer" }}
                >
{index + 1}               
 </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table3;
