import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [apiData, setApiData] = useState(null);

  // สร้างการเชื่อมต่อด้วย EventSource เมื่อ component ถูก mount
  useEffect(() => {
    const eventSource = new EventSource(process.env.REACT_APP_HOST + '/events');

    // การรับข้อมูลแบบ real-time
    eventSource.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setApiData(data);
    };

    // จัดการข้อผิดพลาด
    eventSource.onerror = (error) => {
      console.error('SSE error:', error);
    };

    // คำสั่งที่จะถูกเรียกเมื่อ component ถูก unmount
    return () => {
      eventSource.close();
    };
  }, []);

  // ดึงข้อมูลโดยใช้ axios เมื่อ component ถูก mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST + '/api/ping/check-cctv');
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>CCTV Status</h1>
      {apiData && apiData.data ? (
  <div>
    <table>
      <thead>
        <tr>
          <th>Room</th>
          <th>IP Address</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        {apiData.data.map((room, index) => (
          <React.Fragment key={index}>
            <tr>
              <td colSpan="3">{room.room}</td>
            </tr>
            {room.data.map((item, itemIndex) => (
              <tr key={`${index}-${itemIndex}`}>
                <td></td>
                <td>{item.ip}</td>
                <td>{item.status}</td>
              </tr>
            ))}
          </React.Fragment>
        ))}
      </tbody>
    </table>
  </div>
) : (
  <p>Loading...</p>
)}
    </div>
  );
};

export default App;
