import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/cctv.css';

const App = () => {
  const [apiData, setApiData] = useState(null);
  const [setWebSocketData] = useState(null);

  // Fetch API data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(process.env.REACT_APP_HOST + '/api/ping/check-cctv');
        console.log(process.env.REACT_APP_HOST, response.data);
        setApiData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 2000); // Refresh every 2 seconds

    return () => clearInterval(intervalId); // Cleanup interval on unmount

  }, []);


  // Connect to WebSocket server
  // useEffect(() => {
  //   const ws = new WebSocket('ws://localhost:4000');

  //   console.log('WebSocket :', ws);

  //   ws.onmessage = (event) => {
  //     const data = JSON.parse(event.data);
  //     setWebSocketData(data);
  //     // console.log('set :',data)
  //   };

  //   ws.onclose = () => {
  //     console.log('WebSocket connection closed');
  //   };

  //   ws.onopen = () => {
  //     console.log('WebSocket connection opened');
  //   };

  //   if (ws.readyState === WebSocket.CLOSED) {
  //     console.log('WebSocket connection closed before it was established');
  //   }

  //   return () => {
  //     ws.close();
  //   };
  // }, [setWebSocketData]);

  return (
    <div className="table-container">
      <table className="table">
        <thead className='TTH'>
          <tr>
            {apiData && apiData.data.map(room => (
              room.roomname !== "Suanluang" &&
              room.roomname !== "HYI3_1" &&
              room.roomname !== "HYI3_2" &&
              room.roomname !== "HYI4" &&
              room.roomname !== "OFFICE7" &&
              room.roomname !== "OFFICE6" &&


              <th key={room.roomname} className="room-name">{room.roomname && room.roomname.substring(0, 4)}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          <tr className='bb'>
            {apiData && apiData.data.map(room => (
              room.roomname !== "Suanluang" &&
              room.roomname !== "HYI3_1" &&
              room.roomname !== "HYI3_2" &&
              room.roomname !== "HYI4" &&
              room.roomname !== "OFFICE7" &&
              room.roomname !== "OFFICE6" &&

              <td key={room.roomname}>
                <table className="table">
                  <tbody>
                    {room.status.split(',').map((item, index) => (
                      <tr key={index}>
                        {item.split(',').map((subItem, subIndex) => (
                          <td key={index} className={`status-cell ${item.trim() === 'ON' ? 'status-on' : 'status-off'}`}>
                            <div>{item.trim()}</div>
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </td>
            ))}
          </tr>
        </tbody>
      </table>

      <table className='ST'>
        <tbody>
          {apiData && apiData.data
            .filter(room => ["Suanluang", "HYI3_1", "HYI3_2", "HYI4", "OFFICE7", "OFFICE6"].includes(room.roomname))
            .map(room => (
              <tr key={room.roomname}>
                <td className="room-name">{room.roomname}</td>
                <td className="room-status">
                  <table className="status-table">
                    <tbody>
                      <tr>
                        {room.status.split(',').map((item, index) => (
                          <td key={index} className={`status-cell ${item.trim() === 'ON' ? 'status-on' : 'status-off'}`}>
                            <div>{item.trim()}</div>
                          </td>
                        ))}
                      </tr>
                    </tbody>
                  </table>

                </td>
              </tr>
            ))}
        </tbody>
      </table>



    </div>
  );

};

export default App;
